import React from 'react';

import { isSelected } from "../functions/MainFunctions";

import '../styles/itemsBox.scss';

export default function ItemsBox(props) {

    const {items, setItems, undoItems, setUndoItems, selectedItems, setSelectedItems} = props;

    return (
        <div className='items-box'>
            {
                items.map( item => (
                    <div  className={`item ${isSelected(selectedItems, item)}`} key={item}>
                        <p>{item}</p>
                    </div>
                ))
            }
        </div>
    )
}
